Cookie.dir_path = Path(r"./bing_cookies")


# –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –ø—Ä–∞–≤–∏–ª –∏–∑ —Ç–µ–∫—Å—Ç–æ–≤–æ–≥–æ —Ñ–∞–π–ª–∞
def get_rules(file):
    rules = {}
    with open(file, "r", encoding="utf-8") as f:
        for line in f:
            if line.startswith("#"):
                rule_number, rule_text = line.split(" ", 1)
                rules[rule_number] = rule_text.strip()
    return rules


# –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∏—Å—Ç–æ—Ä–∏–∏ —á–∞—Ç–∞ –∏–∑ —Ç–µ–∫—Å—Ç–æ–≤–æ–≥–æ —Ñ–∞–π–ª–∞
def get_chat_history(file):
    chat_history = ""
    with open(file, "r", encoding="utf-8") as f:
        for line in f:
            chat_history += line
    return chat_history
